function TimedTask(taskFn,delay){this.state=0,this.taskFn=taskFn,this.delay=delay}TimedTask.prototype.start=function(){var self=this;this.windowTimeOut&&window.clearTimeout(this.windowTimeOut),this.windowTimeOut=window.setTimeout(function(){try{var nextDelay=self.taskFn();nextDelay&&(self.delay=nextDelay,self.start())}catch(e){console.log(e)}},self.delay)},TimedTask.prototype.stop=function(){this.windowTimeOut&&window.clearTimeout(this.windowTimeOut)};